import React, { useEffect, useState } from 'react';
const serverURL = "http://localhost:3080";
const Home = () => {
  const [artists, setArtists] = useState([]);
  const [search, setSearch] = useState('');

  // New artist info to be added
  const [addNewArtist, setAddNewArtist] = useState({
    Name: "",
    Born: "",
    Image: "http://127.0.0.1:4000/images/",
    Hometown: "",
    FunFact: "",
    Album: ""
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const artistsResponse = await fetch(serverURL + '/artists');
        const artistsData = await artistsResponse.json();
        setArtists(artistsData);

      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  function handleChange(evt) {
    const value = evt.target.value;
    if (evt.target.name === "Name") {
      setAddNewArtist({ ...addNewArtist, Name: value });
    }
    else if (evt.target.name === "Born") {
      setAddNewArtist({ ...addNewArtist, Born: value });
    }
  }

  return (
    <div>
      <h1>Artists</h1>

      <input className='searchBar'
        type="text"
        placeholder="Search Artists"
        onChange={(e) => setSearch(e.target.value)}
      />

      <h3>Add Artist</h3>

      <form action="">
        <input type="text" placeholder="Name?" name="Name" value={addNewArtist.Name}
          onChange={handleChange} />
        <input type="text" placeholder="Born?" name="Born" value={addNewArtist.Born}
          onChange={handleChange} />
        <input type="text" placeholder="Image?" name="Image" value={addNewArtist.Image}
          onChange={handleChange} />
        <button type="submit">
          Submit New Artist
        </button>
      </form>

      <div className="grid-container">
        {artists.filter((artist) =>
          artist.Name.toLowerCase().includes(search.toLowerCase())
        )
          .map((artist, index) => (
            <div className="grid-item">
              <img src={artist.Image} width="150" height="150" alt={artist.Name} />
              <h3>{artist.Name}</h3>
              <p>Born: {artist.Born}</p>
              <p>Hometown: {artist.Hometown}</p>
              <p>Fun fact: {artist.FunFact}</p>
              <p>Album: {artist.Album}</p>
            </div>
          ))}
      </div>
    </div>
  );

};

export default Home;
