import React, { useEffect, useState } from 'react';
const serverURL = "http://localhost:8080";
const Home = () => {
    const [artists, setArtists] = useState([]);
    const [songs, setSongs] = useState([]);
    var artist_1;
    var artist_2;

    useEffect(() => {
        const fetchData = async () => {
            try {
                const artistsResponse = await fetch(serverURL + '/artistInfo');
                const artistsData = await artistsResponse.json();
                setArtists(artistsData);

                const songsResponse = await fetch(serverURL + '/songInfo');
                const songsData = await songsResponse.json();
                setSongs(songsData);

            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    function shuffleArray(array) {
        for (let i = array.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          [array[i], array[j]] = [array[j], array[i]];
        }
        return array;
      }

    function compareArtists(artist_1, artist_2) {
        var artist_1_score = 0;
        var artist_2_score = 0;

        for (var i = 0; i < songs.length; ++i) {
            if (songs[i].Artist === artist_1) {
                artist_1_score += songs[i].Plays;
            }
            else if (songs[i].Artist === artist_2) {
                artist_2_score += songs[i].Plays;
            }
        }

        if (artist_1_score > artist_2_score) {return 1;}
        else {return 2;}
    }

    function displayArtist() {
        return "Yup";
    }

    function setArtist1String() {
        if (compareArtists(artists[0], artists[1]) === 1) {
            return artists[0].name + "; Winner!";
        }
        return artists[0].name;
    }

    function setArtist2String() {
        if (compareArtists(artists[0], artists[1]) === 2) {
            return artists[1].name + "; Winner!";
        }
        return artists[1].name;
    }

    shuffleArray(artists);

    var artist_1_string = setArtist1String();
    var artist_2_string = setArtist2String();

    return (
        <div>
            <h1>Compare</h1>
            <div className='flexContainer'>
                <div class="homeItems">
                    <h2 className='titleText'>Artist 1</h2>
                    <ul class="infoList">
                        {artists.slice(0, 1).map((artist, index) => (
                            <li key={index}>
                                <div>
                                    <img src={artist.Image} width="150" height="150" alt={artist_1_string} />
                                    <div>
                                        <h3>{artist.Name} </h3>
                                        {/* Add more artist details here if needed */}
                                    </div>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
                <div class="homeItems">
                    <h2 className='titleText'>Artist 2</h2>
                    <ul class="infoList">
                    {artists.slice(1,2).map((artist, index) => (
                            <li>
                                <div>
                                    <img src={artist.Image} width="150" height="150" alt={artist.Name} />
                                    <div>
                                        <h3>{artist.Name}</h3>
                                        {/* Add more artist details here if needed */}
                                    </div>
                                </div>
                            </li>
                    ))}
                    </ul>
                </div>
            </div>
        </div>
    );

};
export default Home;
